package j0114;

public class IfTest_11 { //0105

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 9;
		
		if (a > 5) {
			System.out.println(a + "는 5보다 크다");
		}
		
		
		if(b % 2 ==0) {
			System.out.println(b + "는 짝수입니다.");
		}
		else {
			System.out.println(b + "는 홀수입니다.");
		}
		System.out.println("종료");

		
	}

}
